mapper.select:
SELECT b.*
FROM blogs b


        AND b.id IN (
        SELECT bt.blog_id
        FROM blog_tags bt
        WHERE bt.tag_id IN

            #{itemTagId}

        GROUP BY bt.blog_id
        HAVING COUNT(DISTINCT bt.tag_id) = #{tagCount}
        )



        AND b.title LIKE CONCAT('%', #{title}, '%')


        AND b.user_id = #{userId}


        AND b.create_time >= #{startTime}


        AND b.create_time <= #{endTime}


ORDER BY b.create_time DESC
